import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import io from "socket.io-client";
import "./Home.css";

const socket = io("http://localhost:5000");

function Home() {
  const [gameModes, setGameModes] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      console.log("No token found, redirecting to login");
      navigate("/login");
      return;
    }

    const fetchGameModes = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/game-modes", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setGameModes(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching game modes:", error);
        if (error.response?.status === 401) {
          console.log("Unauthorized token, logging out");
          handleLogout();
        }
        setLoading(false);
      }
    };

    fetchGameModes();

    socket.on("slotUpdate", (updatedGameMode) => {
      setGameModes((prevModes) =>
        prevModes.map((mode) =>
          mode._id === updatedGameMode._id ? updatedGameMode : mode
        )
      );
    });

    return () => socket.off("slotUpdate");
  }, [navigate]);

  const bookSlot = async (gameId) => {
    const token = localStorage.getItem("token");
    if (!token) {
      console.log("No token found, redirecting to login");
      navigate("/login");
      return;
    }

    try {
      await axios.post(
        `http://localhost:5000/api/game-modes/${gameId}/book`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
    } catch (error) {
      console.error("Error booking slot:", error.response?.data?.error || error.message);
      if (error.response?.status === 401) {
        handleLogout();
      }
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  if (loading) return <div className="loading">Loading tournaments...</div>;

  return (
    <div className="home-container">
      <nav className="navbar">
        <Link to="/profile">Profile</Link>
        <Link to="/wallet">Wallet</Link>
        <button onClick={handleLogout} className="logout-btn">Logout</button>
      </nav>
      <h2>Available Tournaments</h2>
      <div className="game-cards">
        {gameModes.length > 0 ? (
          gameModes.map((game) => (
            <div className="game-card" key={game._id}>
              <h3>{game.title}</h3>
              <p>Entry Fee: â‚¹{game.entryFee}</p>
              <p>Slots: {game.filledSlots}/{game.maxSlots}</p>
              <button onClick={() => bookSlot(game._id)} disabled={game.filledSlots >= game.maxSlots}>
                {game.filledSlots >= game.maxSlots ? "Full" : "Book Slot"}
              </button>
            </div>
          ))
        ) : (
          <p>No tournaments available</p>
        )}
      </div>
    </div>
  );
}

export default Home;
