import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Signup.css';

function Signup() {
  const [formData, setFormData] = useState({ username: '', email: '', phone: '', password: '' });
  const [otp, setOtp] = useState('');
  const [showOtpField, setShowOtpField] = useState(false);
  const [loading, setLoading] = useState(false); // Add loading state
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Start loading
    try {
      const res = await axios.post('http://localhost:5000/api/signup', formData, {
        headers: { 'Content-Type': 'application/json' },
      });
      alert(res.data.message);
      setShowOtpField(true);
    } catch (err) {
      alert(err.response?.data?.message || 'Signup failed');
    } finally {
      setLoading(false); // Stop loading regardless of success/failure
    }
  };

  const handleOtpSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Start loading
    try {
      // Optimistic update: Show a message immediately
      alert("Verifying OTP..."); 

      const res = await axios.post('https://ff-game-back-end.vercel.app/api/verify-otp', {
        email: formData.email,
        otp,
      });
      alert(res.data.message);
      navigate('/login', { replace: true });
    } catch (err) {
      alert(err.response?.data?.message || 'OTP verification failed');
    } finally {
      setLoading(false); // Stop loading
    }
  };

  return (
    <div className="signup-container">
      <div className="overlay"></div>
      <button className="home-btn" onClick={() => navigate('/')}>üè† Home</button>
      <div className="signup-card">
        <h2>{showOtpField ? 'Verify OTP' : 'Signup'}</h2>

        {!showOtpField ? (
          <form onSubmit={handleSubmit}>
            {/* ... input groups ... */}
            <button type="submit" className="submit-btn" disabled={loading}>
              {loading ? "Getting OTP..." : "Get OTP"}
            </button>
          </form>
        ) : (
          <form onSubmit={handleOtpSubmit}>
            {/* ... OTP input group ... */}
            <button type="submit" className="submit-btn" disabled={loading}>
              {loading ? "Verifying OTP..." : "Verify OTP"}
            </button>
          </form>
        )}
        <p className="login-link" onClick={() => navigate('/login')}>
          Already have an account? <span>Login here</span>
        </p>
      </div>
    </div>
  );
}

export default Signup;
